// 철자 유사도 검사 함수
export function checkSpellingSimilarity(userAnswer: string, correctAnswer: string): { isSimilar: boolean; similarity: number; } {
  const normalizeText = (text: string) => text.toLowerCase().trim().replace(/\s+/g, '');
  const user = normalizeText(userAnswer);
  const correct = normalizeText(correctAnswer);
  if (user === correct) return { isSimilar: true, similarity: 100 };
  const distance = levenshteinDistance(user, correct);
  const maxLen = Math.max(user.length, correct.length);
  const similarity = ((maxLen - distance) / maxLen) * 100;
  return { isSimilar: similarity >= 80, similarity: Math.round(similarity) };
}

function levenshteinDistance(str1: string, str2: string): number {
  const matrix: number[][] = [];
  for (let i = 0; i <= str2.length; i++) matrix[i] = [i];
  for (let j = 0; j <= str1.length; j++) matrix[0][j] = j;
  for (let i = 1; i <= str2.length; i++) {
    for (let j = 1; j <= str1.length; j++) {
      if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
        matrix[i][j] = matrix[i - 1][j - 1];
      } else {
        matrix[i][j] = Math.min(matrix[i - 1][j - 1] + 1, matrix[i][j - 1] + 1, matrix[i - 1][j] + 1);
      }
    }
  }
  return matrix[str2.length][str1.length];
}
